insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1001, '春咲 日和', 2, 1.4, 0, 0, 0, 1, '我是日向♪\\n今天也要精神十足的加油喔♪', 0, 1, '喔！\\n發現那裡有正在傷腦筋的人！', 0, 1, '騎士先生\\n我們過去幫忙吧～');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1002, '草野 優衣', 2, 1.4, 0, 0, 0, 1, '跟大家一樣\\n我也必須變強一點', 0, 1, '希望可以\\n守護重要的人…', 0, 1, '哎呀，我、\\n我在說些什麼呢\\n啊哈哈…');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1003, '士條 怜', 2, 1.4, 0, 0, 0, 1, '要不要試試看擊劍呢？', 0, 1, '你所需要的注意力與感受', 0, 1, '都可以藉稍微受到磨練喔。');
