insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1006, '風宮 茜里', 2, 1.4, 0, 0, 0, 1, '我是風宮 茜里', 0, 1, '那個…', 0, 1, '你要跟我…\\n一起去嗎？');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1007, '出雲 宮子', 2, 1.5, 0, 0, 0, 1, '我是出雲 宮子ー', 0, 1, '我想吃布丁ー', 0, 1, '我對於食物的怨念可是很恐怖的ー');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1008, '虹村 雪', 2, 1.4, 0, 0, 0, 1, '我是虹村雪。', 0, 1, '你也是因為我的美貌\\n而吸引過來的吧。', 0, 1, '沒關係。\\n被人看，也是我的命運…');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1009, '柊 杏奈', 2, 1.4, 0, 0, 0, 1, '哼…你聽到了可不要後悔！', 0, 1, '我真正的名字是\\n安妮羅潔·馮·修特帕魯！', 0, 1, '人稱「疾風之冥姬」!!');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1010, '姫宮 真步', 2, 1.5, 0, 0, 0, 1, '我是真步真步王國的公主，\\n真步公主', 0, 1, '兔子先生，\\n你讓我遇到命運中的王子', 0, 1, '真的謝謝你了～♪');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1011, '衣之咲 璃乃', 2, 1.4, 0, 0, 0, 1, '我是衣之咲璃乃！', 0, 1, '為了找尋我那離別的哥哥，\\n經歷了許多世界！', 0, 1, '被我遇到了\\n算你命大～！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1012, '柏崎 初音', 2, 1.4, 0, 0, 0, 1, '我是初音，\\n我還蠻強的喔。\\nYeah☆', 0, 1, '什、什麼超能力…\\n那是什麼東西呢～？', 0, 1, '…拜託！\\n不要跟任何人說啊～！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1016, '美波 鈴奈', 2, 1.4, 0, 0, 0, 1, '你好！\\n我是美波鈴奈呦～♪', 0, 1, '勉強算是女子高中生模特兒的時尚教主！', 0, 1, '秀才真的是\\nGF（Ｇood feeling）～♪');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1017, '喜屋武 香織', 2, 1.4, 0, 0, 0, 1, '你好啊。\\n我是喜屋武香織ー。', 0, 1, '東京就像個遊樂園的地方啊ー', 0, 1, '可是，沖繩也是個好地方喔ー');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1018, '支倉 伊緒', 2, 1.4, 0, 0, 0, 1, '我是支倉伊緒。', 0, 1, '還是不太習慣\\n被稱作為老師', 0, 1, '所以就叫我伊緒吧。');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1020, '茜 美美', 2, 1.5, 0, 0, 0, 1, '什麼…？\\n美美，\\n不懂太複雜的事情…', 0, 1, '啊！哥哥～\\n等一下啊～', 0, 1, '不要放美美一個人啊～');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1021, '栗林 胡桃', 2, 1.5, 0, 0, 0, 1, '啊…那個…我是…\\n栗…林… 胡桃…', 0, 1, '那個……\\n咦……', 0, 1, '哇啊啊啊啊啊……');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1022, '風宮 依里', 2, 1.4, 0, 0, 0, 1, '我是風宮依里。\\n啊啊啊啊啊！', 0, 1, '為了我，\\n浪費那麼寶貴的時間，', 0, 1, '總之先謝謝你！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1023, '北條 綾音', 2, 1.5, 0, 0, 0, 1, '我是綾音！\\n我跟噗吉一起跟著你吧！', 0, 1, '哥哥要去哪裡呢？\\n什麼？', 0, 1, '我想要去這裡ー\\n快一點、快一點ー');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1025, '天野 鈴莓', 2, 1.4, 0, 0, 0, 1, '哇…哇…，\\n我叫做天野鈴莓！', 0, 1, '雖然我還不夠成熟，\\n接下來還請您多多…，', 0, 1, '不對，好像怪怪的…\\n啊啊啊，對不起！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1027, '倉石 惠理子', 2, 1.4, 0, 0, 0, 1, '……呵呵…我是\\n…倉石惠理子……', 0, 1, '你是我…命中注定的……\\n另一半……', 0, 1, '我絕對不會………\\n鬆開手的…!!');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1028, '佐佐木 咲戀', 2, 1.4, 0, 0, 0, 1, '我是佐佐木咲戀。\\n你不覺得在這個世界上，\\n無謂的事情太多了嗎？', 0, 1, '如果能節省那些事情的話，\\n應該可以過得更悠閒一點！', 0, 1, '…你不覺得嗎？？');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1029, '櫻井 望', 2, 1.4, 0, 0, 0, 1, '我是櫻井望！\\n請多指教', 0, 1, '那個…\\n你不認識我嗎?!', 0, 1, '哈哈哈，\\n看來我還得努力呢～');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1030, '妮諾‧珠貝爾', 2, 1.4, 0, 0, 0, 1, '鏘鏘！', 0, 1, '初次見面，\\n我叫做妮諾‧珠貝爾！', 0, 1, '將軍，\\n跟著我一起統一天下吧！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1031, '上喜 忍', 2, 1.4, 0, 0, 0, 1, '我是上喜忍。\\n手上拿的骷髏，是我的父親。', 0, 1, '然後在我隔壁的，是……\\n什麼，看不到嗎？', 0, 1, '真是奇怪呢……');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1033, '野戶 真陽', 2, 1.5, 0, 0, 0, 1, '我的目標\\n是成為有名的搞笑藝人！', 0, 1, '但是\\n目前還缺少了點東西…', 0, 1, '沒錯，\\n那就是會吐槽我的對象…！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1034, '綾瀬 優花梨', 2, 1.4, 0, 0, 0, 1, '那個…我是…綾瀬優花…\\n那個…不好意思！', 0, 1, '因為有一點緊張…', 0, 1, '所以，\\n先來去喝一杯了…！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1036, '冰川 鏡華', 2, 1.5, 0, 0, 0, 1, '我…冰川 鏡華。', 0, 1, '因為不可以跟陌生人說話', 0, 1, '所以沒有辦法…\\n在跟你說話了！');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1038, '柏崎 栞', 2, 1.4, 0, 0, 0, 1, '我是柏崎…栞。', 0, 1, '……啊，鬧鐘響了。', 0, 1, '因為我身體太虛弱了，\\n所以每三十分鐘，\\n就得要休息一次。');
insert into v1_644ab5ebef7ea94c45b8998818ef43218826581fd1f6f27fc739c7e8518d91cf values (1040, '雙葉 碧', 2, 1.4, 0, 0, 0, 1, '我、我是雙葉碧。', 0, 1, '我，從來都沒有交過朋友……', 0, 1, '只有花朵，\\n是我唯一的說話對象。');
