insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24007, 24, 192301, 2, '社員3', '這點程度輕鬆得很，\\n畢竟我們可是\\n以全國大賽為目標啊。', 801, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 2, 10046);
