insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24013, 24, 113501, 6, '美里', '哎呀～該不會我\\n不小心贏了嗎？\\n可能是因為我有\\n看著大家練習吧～', 111, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 5.33, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24014, 24, 113501, 1, '美里', '老師想幫上大家的忙，\\n所以也努力試試看了～！\\n看來似乎有順利丟出去，\\n太好了～！', 112, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 6.85, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24015, 24, 113501, 1, '美里', '練習辛苦了～！\\n老師也很高興\\n能夠幫上忙喔～♪\\n接下來也一起努力加油吧～！', 113, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 3.0, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24016, 24, 192401, 1, '大地', '看來練習得還不夠啊，\\n我甚至沒有\\n拿出真本事投呢，\\n再練一次！', 611, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 6.11, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24017, 24, 192401, 1, '大地', '哼，這是理所當然的結果。\\n我的球才不會讓任何人打到！\\n要是還想繼續打，\\n我就奉陪到底！', 612, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 6.75, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24018, 24, 192401, 2, '大地', '很好！\\n就以這股氣勢，朝全國前進吧！\\n看我用三振解決掉所有對手！', 613, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24019, 24, 113401, 2, '初音', '閃亮亮☆\\n初音大勝利喲～！\\n為了讓大家能更加進步，\\n我也會繼續努力下去的！', 211, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 4.52, 1, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24020, 24, 113401, 2, '初音', '好耶！我贏了！\\n不管是啦啦隊還是投手，\\n都交給我初音吧！', 212, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 3.89, 1, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24021, 24, 113401, 1, '初音', '很好～就像這樣繼續\\n努力吧……雖然是這麼想，\\n但因為用了超能力……\\n我開始想睡了……呼嚕……', 213, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 13.49, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24022, 24, 106501, 1, '嘉夜', '真是場好比賽！\\n我太熱血沸騰，\\n不小心就用全力丟球了。', 311, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 3.17, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24023, 24, 106501, 1, '嘉夜', '如何？\\n老娘的球很熾熱吧！\\n不過，你們的鬥志\\n也是挺不錯的喔。', 312, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 4.42, 2, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24024, 24, 106501, 6, '嘉夜', '什麼嘛，就這點程度而已嗎？\\n我還完全沒投個夠呢，\\n再繼續比下去吧！', 313, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 2.89, 1, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24025, 24, 102301, 4, '綾音', '差一點點了說～！\\n下次我一定會跟噗吉一起\\n贏給你們看的！', 421, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 3.71, 3, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24026, 24, 192201, 2, '旭', '真是一場好比賽呢！\\n不過果然還是好不甘心……\\n我會再多做練習，\\n下次絕對會是我贏！', 721, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 5.1, 1, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24027, 24, 192301, 1, '社員3', '嘖……還差一步啊。\\n下次我一定會贏的，\\n做好心理準備吧。', 821, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 4.34, 3, 10046);
insert into v1_73fa1026e4598929ba4a7485abe1e859c40b717e83896bdbb7e99e8402ab402e values (24028, 24, 192501, 1, '', '真是一場好比賽，\\n下次再比一次吧。', 521, '2020/07/31 12:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 1, 10046);
