insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (14001, 14, 106001, 1, '凱留', '攻略冒險的話，\\n聽說就能得到道具\\n能拿的東西，\\n我們就要拿到！', 1, '2015/12/17 15:00:00', '2099/12/17 14:59:59', -363.0, 0.0, 0.0, 1, 0, 0);
