insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (17002, 17, 105901, 1, '可可蘿', ' 在這邊可以回顧\\n與同伴一同參加戰隊競賽時\\n大顯身手的樣子。', 1, '2015/12/17 15:00:00', '2099/12/17 14:59:59', -363.0, 0.0, 0.0, 1, 0, 0);
