insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (22001, 22, 190801, 1, '花凜', '投票請到這邊。\\n請為你支持的女孩\\n投下你心意滿滿的\\n一票吧！', 1, '2019/07/24 16:00:00', '2030/12/17 14:59:59', -430.0, 0.0, 0.0, 1, 0, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (22002, 22, 190801, 1, '花凜', '歡迎！\\n你喜愛的女孩\\n是哪一位呢？\\n請幫她加油吧！', 2, '2019/07/24 16:00:00', '2030/12/17 14:59:59', -430.0, 0.0, 0.0, 1, 0, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (23001, 23, 190801, 1, '花凜', '謝謝你們\\n踴躍的投票。\\n請大家耐心等候，\\n敬請期待結果發表喔！', 3, '2019/07/24 16:00:00', '2030/12/17 14:59:59', -430.0, 0.0, 0.0, 1, 0, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (23002, 23, 190801, 1, '花凜', '目前正在統計\\n人氣投票結果。\\n在結果統計完畢之前\\n請再稍候一下喔。', 4, '2019/07/24 16:00:00', '2030/12/17 14:59:59', -430.0, 0.0, 0.0, 1, 0, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24001, 24, 109001, 1, '惠理子（情人節）', '有大人您幫忙製作的\\n巧克力蛋糕。\\n一定會很美味的。', 1, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 4.08, 2, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24002, 24, 109001, 1, '惠理子（情人節）', '這種失誤絕對不是\\n大人您會犯的。\\n再挑戰一次看看吧。', 2, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 5.0, 2, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24003, 24, 109001, 1, '惠理子（情人節）', '原本要為了大人您而製作的蛋糕，\\n結果卻讓您來幫忙……。\\n事情竟然變成這樣……嘻嘻嘻嘻。', 3, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 10.11, 2, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24004, 24, 109001, 1, '惠理子（情人節）', '您不需要太過煩惱。\\n今後我會花費許多時間\\n來教導您的。', 4, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 4.0, 2, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24005, 24, 109001, 1, '惠理子（情人節）', '您這次的狀態不太好……嗎？\\n如果是大人您幫忙做的話，\\n不論是什麼我都……嘻嘻嘻嘻。', 5, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 6.0, 2, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24006, 24, 109002, 1, '惠理子（情人節）', '大人您親自製作的巧克力蛋糕\\n對我來說是寶物……\\n膽敢取笑你的人是不會有活路的。', 6, '2019/07/01 16:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 1, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24007, 24, 109002, 1, '惠理子（情人節）', '這賣相做得實在太完美了！\\n做出了非常符合\\n大人您氣質的巧克力蛋糕。', 7, '2019/07/01 16:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 1, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24008, 24, 109002, 1, '惠理子（情人節）', '幫忙了我的大人您\\n已然是我的俘虜了……\\n讓我來邀請您蒞臨這甜蜜的世界。', 8, '2019/07/01 16:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 1, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24009, 24, 109002, 1, '惠理子（情人節）', '您的愛情我確實地收下了。\\n這是……我們第一次的\\n共同作業呢……嘻嘻嘻嘻。', 9, '2019/07/01 16:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 1, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24010, 24, 109002, 1, '惠理子（情人節）', '這是世界第一的巧克力蛋糕。\\n因為這是大人您\\n拿出看家本領製作的嘛。', 10, '2019/07/01 16:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 1, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24011, 24, 109101, 1, '靜流（情人節）', '做得不太好覺得很可惜嗎？\\n不過，弟弟的心意讓我很開心呢。\\n姐姐會一直陪在你身邊的♪', 1, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 2.12, 2, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24012, 24, 109101, 5, '靜流（情人節）', '嗯，弟弟難得的\\n緊張了吧？\\n要再挑戰一次看看嗎？', 2, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 4.19, 2, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24013, 24, 109101, 2, '靜流（情人節）', '你幫了姐姐的忙，\\n做得很棒喔！\\n啊，還不可以偷吃啦！', 3, '2019/07/01 16:00:00', '2030/12/17 14:59:59', -385.0, 8.0, 2.3, 4, 10011, 0);
insert into v1_cd169e098455aa74aecd6dd8da2cc791dc8163d262a8001c5601f18c630b958c values (24014, 24, 109102, 1, '靜流（情人節）', '你看你看！\\n海綿蛋糕的顏色烤得很美喔。\\n不愧是弟弟呢！', 4, '2019/07/01 16:00:00', '2030/12/17 14:59:59', 0.0, 0.0, 0.0, 1, 10011, 0);
